// components/NewTaskButton.js
import React, { useState } from "react";

const NewTaskButton = () => {
  const [showForm, setShowForm] = useState(false);

  const handleButtonClick = () => {
    setShowForm(!showForm);
  };

  return (
    <div style={{ position: "fixed", bottom: "20px", right: "20px" }}>
      <button
        onClick={handleButtonClick}
        style={{
          height: "50px",
          width: "50px",
          borderRadius: "50%",
          fontSize: "24px",
          textAlign: "center",
          lineHeight: "50px",
        }}
      >
        +
      </button>
      {showForm && (
        <div
          style={{
            position: "absolute",
            bottom: "60px",
            right: "0",
            padding: "10px",
            border: "1px solid #ccc",
            backgroundColor: "#5db6ff",
          }}
        >
          <input
            type="text"
            placeholder="Title"
            style={{ display: "block", margin: "5px 0" }}
          />
          <input type="date" style={{ display: "block", margin: "5px 0" }} />
          <input type="time" style={{ display: "block", margin: "5px 0" }} />
          <button>Create Task</button>
        </div>
      )}
    </div>
  );
};

export default NewTaskButton;
